mapper.insert:
INSERT INTO web.channel(
	ch_name
	, ch_img
	, ch_memo
	, ch_st_dt
	, ch_end_dt
	, ch_pass
	, ch_open_yn
	, cret_dt
	, cret_id
)VALUES(
	#{chName}
	, #{chImg}
	, #{chMemo}
	, #{chStDt}::timestamp
	, #{chEndDt}::timestamp
	, #{chPass}
	, #{chOpenYn}::bool
	, NOW()
	, #{cretId}
);

mapper.update:
update web.channel set 
	ch_name = #{chName}
	, ch_img = #{chImg}
	, ch_memo = #{chMemo}
	, ch_st_dt = #{chStDt}::timestamp
	, ch_end_dt = #{chEndDt}::timestamp
	, ch_pass = #{chPass}
	, ch_open_yn = #{chOpenYn}::bool
	, updt_dt = NOW()
	, updt_id = #{updtId}
where ch_seq = #{chSeq}::numeric

mapper.insert:
